<template>
  <view
    class="commonContainer"
    :style="{ overflow: scroll ? 'scroll' : 'visible' }"
  >
    <view class="title">
      <view v-if="showBack && !showHome" class="backIcon" @click.stop="goBack">
        <u-icon size="20" name="arrow-left"></u-icon>
      </view>
      <view v-if="showBack && showHome" class="backIcon" @click.stop="goHome">
        <u-icon size="28" name="home"></u-icon>
      </view>
      <!-- <u-icon class="backIcon" size="20" name="arrow-left"></u-icon> -->
      <slot name="search"></slot>
      {{ title }}
    </view>
    <view :class="showPadding ? 'contentPaddingBox' : 'contentBox'">
      <slot></slot>
    </view>
  </view>
</template>

<script>
export default {
  name: "CommonContainer",
  props: {
    title: {
      type: String,
      default: "",
    },
    showBack: {
      type: Boolean,
      default: true,
    },
    showPadding: {
      type: Boolean,
      default: true,
    },
    scroll: {
      type: Boolean,
      default: true,
    },
  },
  data() {
    return {
      showHome: false,
    };
  },
  mounted() {
    this.init();
  },
  methods: {
    init() {
      this.showHome = getCurrentPages().length === 1;
    },
    goBack() {
      uni.navigateBack();
    },
    goHome() {
      uni.switchTab({
        url: "/pages/home/index/index",
      });
    },
  },
};
</script>

